
public class ReverseOfArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[]  a2;
		a2= new int[10];
	
		for(int i=0;i<10;i++){
			a2[i]=i;
		System.out.println(a2[i]);
		
			}
		System.out.println("--------------------------");
		int i;
		int[] a1;
		a1=new int[10];
		for(i=9;i>0;i--){
			a1[i]=i;
			
		System.out.println(a1[i]);

	}

	}}
